import { Box, Typography, Button, CircularProgress } from "@mui/material";
import useAxios from "../hooks/useAxios";
import { useSelector } from "react-redux";
import { useState } from "react";

const Questions = () => {
  const {
    question_category,
    question_difficulty,
    question_type,
    amount_of_questions,
  } = useSelector((state) => state);

  let apiUrl = `/api.php?amount=${amount_of_questions}`;

  if (question_category) {
    apiUrl = apiUrl.concat(`&category=${question_category}`);
  }

  if (question_difficulty) {
    apiUrl = apiUrl.concat(`&difficulty=${question_difficulty}`);
  }

  if (question_type) {
    apiUrl = apiUrl.concat(`&type=${question_type}`);
  }

  const { response, loading } = useAxios({ url: apiUrl });
  const { questionIndex, setQuestionIndex } = useState(0);


  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  console.log("Log in questions: " + response);
  console.log("response.results[0]: " + response.results[0]);

  return (
    <>
    {response.results.splice(0,amount_of_questions).map(()=>
      <Box>
      <Typography variant="h4">Question {}</Typography>
      <Typography mt={5}>
        {response.response[questionIndex].question}
      </Typography>
      <Box mt={2}>
        <Button variant="contained">Answer 1</Button>
      </Box>
      <Box mt={2}>
        <Button variant="contained">Answer 2</Button>
      </Box>
      <Box mt={5}>Score: 2/5</Box>
    </Box>
      )}
    </>
  );
};

export default Questions;
