import { Box, Typography, Button, CircularProgress } from "@mui/material";
import useAxios from "../hooks/useAxios";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import { handleScoreChange } from "../redux/actions";
import { decode } from "html-entities";

const getRandomInt = (max) => {
  return Math.floor(Math.random() * max);
};

const Questions = () => {
  const {
    question_category,
    question_difficulty,
    question_type,
    // amount_of_questions,
    score,
  } = useSelector((state) => state);

  const history = useNavigate();
  const dispatch = useDispatch();

  const [questionIndex, setQuestionIndex] = useState(0);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    console.log("response in useEffect " + response);
    let apiUrl = `/api.php?amount=10&category=${question_category}&difficulty=${question_difficulty}&type=${question_type}`;
  const { response, loading } = useAxios({ url: apiUrl });
    // if (response?.results.length) {
      if (response) {
      const question = response.results[questionIndex];
      let answers = [...question.incorrect_answers];
      answers.splice(
        getRandomInt(question.incorrect_answers.length + 1),
        0,
        question.correct_answer
      );
      setOptions(answers);
    } 
  }, [response, questionIndex, history]);

  if (loading) {
    return (
      <Box mt={20}>
        <CircularProgress />
      </Box>
    );
  }

  const handleClickAnswer = (e) => {
    const question = response.results[questionIndex];
    if (e.target.textContent === question.correct_answer) {
      dispatch(handleScoreChange(score + 1));
    }

    if (questionIndex + 1 < response.results.length) {
      setQuestionIndex(questionIndex + 1);
    } else {
      history("/score");
    }
  };

  return (
    <>
      {/* { (response.results.length!==0) ? ( */}
      <Box>
        <Typography variant="h4">Question {questionIndex + 1}</Typography>
        <Typography mt={5}>
          {decode(response.results[questionIndex].question)}
        </Typography>
        <Box mt={5}>
          Score: {score}/{response.results.length}
        </Box>
        {options.map((data, id) => (
          <Box mt={2} key={id}>
            <Button onClick={handleClickAnswer} variant="contained">
              {decode(data)}
            </Button>
          </Box>
        ))}
      </Box>

      {/* ) : ( history("/issue")) }  */}
    </>
  );
};

export default Questions;
